srcDoc.css:
.app {
  width: calc(1px * var(--appsmith-ui-width));
  justify-content: center;
  border-radius: 0px;
  border: none;
}
.app .tip-container {
  margin-bottom: 20px;
}
.app .tip-container h2 {
  margin-bottom: 20px;
  font-size: 16px;
  font-weight: 700;
}
.app .tip-container .tip-header {
  display: flex;
  justify-content: space-between;
  align-items: baseline;
}
.app .tip-container .tip-header div {
  color: #999;
}
.app .button-container {
  text-align: right;
}
.app .button-container button {
  margin: 0 10px;
  border-radius: var(--appsmith-theme-borderRadius) !important;
}
.app .button-container button.primary {
  background: var(--appsmith-theme-primaryColor) !important;
}
.app .button-container button.reset:not([disabled]) {
  color: var(--appsmith-theme-primaryColor) !important;
  border-color: var(--appsmith-theme-primaryColor) !important;
}

