  font-family: "Roboto", sans-serif;
}

body {
  margin: 0;
  padding: 0;
}

#map {
  position: absolute;
  top: 0;
  bottom: 0;
  width: 100%;
}

.search-box {
  position: absolute;
  top: 30px;
  left: 60px;
  display: flex;
  align-items: center;
}

.color-white {
  color: white;
  /* width: 70px; */
  /* height: 35px; */
}

.search-box-input {
  width: 350px;
  min-height: 35px;
  border-left: none;
  padding: 1px 8px;
  box-shadow: 1px 1px 1px rgba(0, 0, 0, 0.1);
  outline: none;
  border-radius: 2px 0 0 2px;
  border: 1px solid transparent;
}

.search-box-btn {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 40px;
  height: 39px;
  background-color: #fdffff;
  box-shadow: 1px 1px 0 rgba(0, 0, 0, 0.1);
