.clear-box {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 20px;
  height: 39px;
  background-color: #fdffff;
  box-shadow: 1px 1px 0 rgba(0, 0, 0, 0.1);
  cursor: pointer;
  /* border-left: 1px solid #cccece; */
}

.direction-box-btn {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 40px;
  height: 39px;
  background-color: #fdffff;
  box-shadow: 1px 1px 0 rgba(0, 0, 0, 0.1);
  border-radius: 0 2px 2px 0;
  cursor: pointer;
  border-left: 1px solid #cccece;
}

.search-result {
  position: absolute;
  top: 44px;
  width: 432px;
  background-color: white;
  box-shadow: inset 1px 1px 0 rgba(0, 0, 0, 0.1), inset 0 -1px 0 rgba(0, 0, 0, 0.07);
  z-index: 2;
  border-radius: 2px;
  padding: 8px;
  display: none;
}

.result-box {
  width: 100%;
  cursor: pointer;
  margin: 0 0 8px 0;
}

.result-box:hover {
  background-color: rgb(221, 221, 221);
}

.display-none {
  display: none;
}

.input-direction-container {
  /* margin: 30px 0; */
  position: absolute;
  display: none;
  left: 60px;
  top: 30px;
}

.close-directions {
  margin-left: 10px;
}

.direction {
  width: 100px;
  height: 40px;
}

.search-box-direction {
  width: 350px;
  height: 35px;
  border-left: none;
  padding: 1px 8px;
  box-shadow: 1px 1px 1px rgba(0, 0, 0, 0.1);
  outline: none;
  border-radius: 2px 0 0 2px;
  border: 1px solid transparent;
}

#start,
#end {
  width: 300px;
  /* padding: 15px; */
  margin-right: 10px;
  /* margin-left: 10px; */
}

#get-directions {
  padding: 5px 10px;
}

#coordinates {
  background: rgba(0, 0, 0, 0.5);
  color: #fff;
  position: absolute;
  bottom: 10px;
  left: 10px;
  padding: 5px 10px;
  margin: 0;
  font-size: 11px;
  line-height: 18px;
  border-radius: 3px;
  display: none;
}

.popover-button {
  position: absolute;
  padding: 10px;
  cursor: pointer;
  border: 1px solid #ccc;
  display: inline-block;
  background-color: #f9f9f9;
  left: 60px;
  top: 80px;
  width: 95px;
}

.popover {
  left: 60px;
  top: 125px;
  display: none;
  position: absolute;
  background-color: #fff;
  border: 1px solid #ccc;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
  padding: 10px;
  z-index: 1000;
}

.popover .map-style-option {
  padding: 10px;
  cursor: pointer;
  border-bottom: 1px solid #eee;
}

.popover .map-style-option:last-child {
  border-bottom: none;
}

.popover .map-style-option:hover {
  background-color: #f1f1f1;
}

.search-result-start {
  position: absolute;
  top: 44px;
  width: 432px;
  background-color: white;
  box-shadow: inset 1px 1px 0 rgba(0, 0, 0, 0.1), inset 0 -1px 0 rgba(0, 0, 0, 0.07);
  z-index: 2;
  border-radius: 2px;
  padding: 8px;
  display: none;
}

.search-result-end {
  position: absolute;
  top: 44px;
  width: 432px;
  background-color: white;
  box-shadow: inset 1px 1px 0 rgba(0, 0, 0, 0.1), inset 0 -1px 0 rgba(0, 0, 0, 0.07);
  z-index: 2;
  border-radius: 2px;
  padding: 8px;
  display: none;
}

srcDoc.js:
appsmith.onReady(() => {
  const apiUrl = appsmith.model.GOONG_API_URL;
  const apiKey = appsmith.model.GOONG_API_KEY; // Goong API key
  const mapKey = appsmith.model.GOONG_MAP_TILES_API_KEY;
  const accessMapboxToken = appsmith.model.MAPBOX_ACCESS_TOKEN; // Mapbox access token
  const center = [105.85242472181584, 21.029579719995272]; // Initial map center
  const zoom = 14;
  mapboxgl.accessToken = accessMapboxToken;
  const map = new mapboxgl.Map({
    container: 'map',
    style: `https://tiles.goong.io/assets/goong_map_web.json?api_key=${mapKey}`,
    zoom: zoom,
    center: center
  });
  map.on('load', () => {
    main();
  });
  function main() {
    function drawCircle(center, radiusInMeters) {
      const points = 74;
      const coords = {
        latitude: center[1],
        longitude: center[0]
      };
      const km = radiusInMeters / 1000;
      const ret = [];
      const distanceX = km / (111.320 * Math.cos(coords.latitude * Math.PI / 180));
      const distanceY = km / 110.574;
      let theta, x, y;
      for (let i = 0; i < points; i++) {
        theta = i / points * (2 * Math.PI);
        x = distanceX * Math.cos(theta);
        y = distanceY * Math.sin(theta);
        ret.push([coords.longitude + x, coords.latitude + y]);
      }
      ret.push(ret[0]);
      return ret;
    }
    map.addControl(new mapboxgl.NavigationControl());
    let currentMarker = null;

    // Function to add the circle to the map (after style is loaded)
    function addCircle(lngLat, radius) {
      // Create GeoJSON data for the circle
      const circleData = {
        'type': 'FeatureCollection',
        'features': [{
          'type': 'Feature',
          'geometry': {
            'type': 'Polygon',
            'coordinates': [drawCircle(lngLat, radius)] // Generate circle coordinates
          }
        }]
      };
      // 
      // // Check if the source already exists to avoid duplicate sources
      if (!map.getSource('circle')) {
        map.addSource('circle', {
          'type': 'geojson',
          'data': circleData
        });
      } else {
        // // If the source already exists, update the data (this is useful for dynamic updates)
        map.getSource('circle').setData(circleData);
      }
      // Add the layer for the circle if it hasn't been added yet
      if (!map.getLayer('circle')) {
        map.addLayer({
          'id': 'circle',
          'type': 'fill',
          // Use 'fill' for the polygon
          'source': 'circle',
          'layout': {},
          'paint': {
            'fill-color': '#588888',
            // Set the circle color
            'fill-opacity': 0.5 // Set the opacity
          }
        });
      }
    }
    // Add or update the circle on model change
    appsmith.onModelChange(() => {
      const lngLat = [appsmith.model.locationState.longitude, appsmith.model.locationState.latitude];
      addMarker(lngLat);
      map.flyTo({
        center: lngLat
      });
      appsmith.triggerEvent("onUpdateLocation");
    });

    // Function to add or replace a marker on the map
    function addMarker(lngLat) {
      if (currentMarker) {
        currentMarker.remove(); // Remove existing marker
      }
      currentMarker = new mapboxgl.Marker().setLngLat(lngLat).addTo(map);

      // Check if the map style is loaded
      if (map.isStyleLoaded()) {
        // Proceed with adding the source and layer if style is loaded
        addCircle(lngLat, appsmith.model.currentRadius * 1000); // km => meters
      } else {
        // If the style is not loaded, wait for it to load
        map.once('styledata', function () {
          // Now the style is loaded, proceed with adding the source and layer
          // addCircle(lngLat, radius);
        });
      }
    }

    // Click event to add marker on the map
    map.on('click', e => {
      const {
        lng,
        lat
      } = e.lngLat;
      appsmith.triggerEvent("onLocationStateUpdate", {
        lat: lat,
        lon: lng
      });
    });

    // Search input functionality
    document.getElementById('input').addEventListener('input', function () {
      const query = this.value;
      if (query.length >= 2) {
        fetchDataAutoComplete(query);
      } else {
        renderArray([]);
      }
    });

    // Function to fetch data from Goong API for search autocomplete
    function fetchDataAutoComplete(query) {
      const apiLink = `${apiUrl}/Place/AutoComplete?api_key=${apiKey}&input=${encodeURIComponent(query)}`;
      fetch(apiLink).then(response => response.json()).then(data => {
        if (data.predictions) {
          renderArray(data.predictions);
        } else {
          renderArray([]);
        }
      }).catch(error => {
        console.error('Error fetching data:', error);
        renderArray([]);
      });
    }

    // Render autocomplete results
    function renderArray(data) {
      const resultContainer = document.getElementById('result');
      const inputContainer = document.getElementById('input');
      resultContainer.innerHTML = '';
      if (data.length > 0) {
        resultContainer.style.display = 'block';
      } else {
        resultContainer.style.display = 'none';
      }
      data.forEach(item => {
        const div = document.createElement('div');
        div.className = 'result-box';
        div.textContent = item.description;
        div.addEventListener('click', function () {
          fetchPlaceDetails(item.place_id);
          resultContainer.style.display = 'none';
          inputContainer.value = item.description;
        });
        resultContainer.appendChild(div);
      });
    }

    // Fetch details of the place from the Goong API
    function fetchPlaceDetails(placeId) {
      const apiLink = `${apiUrl}/Place/Detail?api_key=${apiKey}&place_id=${placeId}`;
      fetch(apiLink).then(response => response.json()).then(data => {
        if (data.result) {
          const {
            location
          } = data.result.geometry;
          const lngLat = [location.lng, location.lat];
          addMarker(lngLat); // Add marker at the searched location
          map.flyTo({
            center: lngLat,
            zoom: 14
          });
        } else {
          console.error('No result found for place details');
        }
      }).catch(error => {
        console.error('Error fetching place details:', error);
      });
    }
  }
});

